import React, { createContext, useContext, useReducer, useEffect } from 'react';
import type { ReactNode } from 'react';
import { useLocation } from 'wouter';
import axios from 'axios';
import type { AxiosInstance, AxiosResponse } from 'axios';

// Types
export interface User {
  id: string;
  email: string;
  role: 'CUSTOMER' | 'VENDOR' | 'ADMIN' | 'SUPPLIER' | 'EVENT_COORDINATOR' | 'DROPOFF';
  profile?: {
    firstName: string;
    lastName: string;
    phone?: string;
    bio?: string;
    website?: string;
    businessName?: string;
  } | null;
}

export interface AuthState {
  user: User | null;
  isAuthenticated: boolean;
  isLoading: boolean;
  error: string | null;
}

export interface AuthContextType extends AuthState {
  login: (email: string, password: "***REDACTED***" => Promise<void>;
  signup: (userData: SignupData) => Promise<void>;
  logout: () => Promise<void>;
  fetchUser: () => Promise<void>;
  clearError: () => void;
}

export interface SignupData {
  email: string;
  password: "***REDACTED***"
  role: 'CUSTOMER' | 'VENDOR' | 'ADMIN' | 'SUPPLIER' | 'EVENT_COORDINATOR' | 'DROPOFF';
  firstName: string;
  lastName: string;
  phone?: string;
  bio?: string;
  website?: string;
}

// Action types
type AuthAction =
  | { type: 'AUTH_START' }
  | { type: 'AUTH_SUCCESS'; payload: User }
  | { type: 'AUTH_FAILURE'; payload: string }
  | { type: 'LOGOUT' }
  | { type: 'CLEAR_ERROR' }
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'FETCH_USER_START' }
  | { type: 'FETCH_USER_SUCCESS'; payload: User }
  | { type: 'FETCH_USER_FAILURE'; payload: string | null };

// Initial state
const initialState: AuthState = {
  user: null,
  isAuthenticated: false,
  isLoading: true,
  error: null,
};

// Reducer
const authReducer = (state: AuthState, action: AuthAction): AuthState => {
  switch (action.type) {
    case 'AUTH_START':
      return {
        ...state,
        isLoading: true,
        error: null,
      };
    case 'AUTH_SUCCESS':
      return {
        ...state,
        user: action.payload,
        isAuthenticated: true,
        isLoading: false,
        error: null,
      };
    case 'AUTH_FAILURE':
      return {
        ...state,
        user: null,
        isAuthenticated: false,
        isLoading: false,
        error: action.payload,
      };
    case 'LOGOUT':
      return {
        ...state,
        user: null,
        isAuthenticated: false,
        isLoading: false,
        error: null,
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null,
      };
    case 'SET_LOADING':
      return {
        ...state,
        isLoading: action.payload,
      };
    case 'FETCH_USER_START':
      return {
        ...state,
        isLoading: true,
        error: null,
      };
    case 'FETCH_USER_SUCCESS':
      return {
        ...state,
        user: action.payload,
        isAuthenticated: true,
        isLoading: false,
        error: null,
      };
    case 'FETCH_USER_FAILURE':
      return {
        ...state,
        user: null,
        isAuthenticated: false,
        isLoading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

// Create context
const AuthContext = createContext<AuthContextType | undefined>(undefined);

// API base URL
const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:3001/api';

// Set global Axios defaults
axios.defaults.withCredentials = true;

// Create Axios instance with default configuration
const api: AxiosInstance = axios.create({
  baseURL: API_BASE_URL,
  withCredentials: true, // Important for sending cookies with requests
  headers: {
    'Content-Type': 'application/json',
  },
});

// Axios response interceptor for error handling
api.interceptors.response.use(
  (response: AxiosResponse) => response,
  (error) => {
    if (error.response?.status === 401) {
      // Handle unauthorized responses
      console.log('Unauthorized request, user may need to login');
    }
    return Promise.reject(error);
  }
);

// Auth provider component
export const AuthProvider: React.FC<{ children: ReactNode }> = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);
  const [, setLocation] = useLocation();

  // Fetch user on mount
  useEffect(() => {
    fetchUser();
  }, []);

  const fetchUser = async () => {
    try {
      dispatch({ type: 'FETCH_USER_START' });

      const response = await api.get('/auth/session');

      if (response.data.authenticated && response.data.user) {
        dispatch({ type: 'FETCH_USER_SUCCESS', payload: response.data.user });
      } else {
        // Don't treat this as an error - just no active session
        dispatch({ type: 'FETCH_USER_FAILURE', payload: null });
      }
    } catch (error) {
      // Only log the error, don't treat 401 as a failure since it's expected when not logged in
      if (axios.isAxiosError(error) && error.response?.status === 401) {
        dispatch({ type: 'FETCH_USER_FAILURE', payload: null });
      } else {
        console.error('Fetch user error:', error);
        const errorMessage = error instanceof Error ? error.message : 'Failed to fetch user';
        dispatch({ type: 'FETCH_USER_FAILURE', payload: errorMessage });
      }
    }
  };

  const login = async (email: string, password: "***REDACTED***" => {
    try {
      dispatch({ type: 'AUTH_START' });

      const response = await api.post('/auth/login', { email, password });

      if (response.data.user) {
        dispatch({ type: 'AUTH_SUCCESS', payload: response.data.user });
      } else {
        throw new Error('Login response missing user data');
      }
    } catch (error) {
      console.error('Login error:', error);
      let errorMessage = 'Login failed';

      if (axios.isAxiosError(error)) {
        errorMessage = error.response?.data?.message || error.response?.data?.error || 'Login failed';
      } else if (error instanceof Error) {
        errorMessage = error.message;
      }

      dispatch({ type: 'AUTH_FAILURE', payload: errorMessage });
      throw new Error(errorMessage);
    }
  };

  const signup = async (userData: SignupData) => {
    try {
      dispatch({ type: 'AUTH_START' });

      const response = await api.post('/auth/register', userData);

      if (response.data.user) {
        dispatch({ type: 'AUTH_SUCCESS', payload: response.data.user });
      } else {
        throw new Error('Registration response missing user data');
      }
    } catch (error) {
      console.error('Signup error:', error);
      let errorMessage = 'Registration failed';

      if (axios.isAxiosError(error)) {
        if (error.response?.data?.details) {
          // Handle validation errors
          const validationErrors = error.response.data.details
            .map((err: any) => `${err.field}: ${err.message}`)
            .join(', ');
          errorMessage = `Validation failed: ${validationErrors}`;
        } else {
          errorMessage = error.response?.data?.message || error.response?.data?.error || 'Registration failed';
        }
      } else if (error instanceof Error) {
        errorMessage = error.message;
      }

      dispatch({ type: 'AUTH_FAILURE', payload: errorMessage });
      throw new Error(errorMessage);
    }
  };

  const logout = async () => {
    try {
      await api.post('/auth/logout');
    } catch (error) {
      console.error('Logout error:', error);
      // Continue with logout even if the request fails
    } finally {
      dispatch({ type: 'LOGOUT' });
      setLocation('/');
    }
  };

  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  const value: AuthContextType = {
    ...state,
    login,
    signup,
    logout,
    fetchUser,
    clearError,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
};

// Custom hook to use auth context
export const useAuth: "***REDACTED***" AuthContextType => {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

import React, { createContext, useContext, useReducer, useEffect } from 'react';

interface Product {
  id: string;
  name: string;
  description?: string;
  price: number;
  imageUrl?: string;
  tags: string[];
  stock: number;
  isAvailable: boolean;
  targetMargin?: number;
  recipeId?: string;
  onWatchlist: boolean;
  lastAiSuggestion?: number;
  aiSuggestionNote?: string;
  createdAt: string;
  updatedAt: string;
  vendorProfileId: string;
}

interface CartItem {
  product: Product;
  quantity: number;
}

interface CartState {
  items: CartItem[];
  isOpen: boolean;
}

type CartAction =
  | { type: 'ADD_ITEM'; payload: { product: Product; quantity: number } }
  | { type: 'REMOVE_ITEM'; payload: { productId: string } }
  | { type: 'UPDATE_QUANTITY'; payload: { productId: string; quantity: number } }
  | { type: 'CLEAR_CART' }
  | { type: 'TOGGLE_CART' }
  | { type: 'CLOSE_CART' }
  | { type: 'LOAD_CART'; payload: CartItem[] };

interface CartContextType {
  state: CartState;
  addItem: (product: Product, quantity: number) => void;
  removeItem: (productId: string) => void;
  updateQuantity: (productId: string, quantity: number) => void;
  clearCart: () => void;
  toggleCart: () => void;
  closeCart: () => void;
  getItemQuantity: (productId: string) => number;
  getTotalItems: () => number;
  getSubtotal: () => number;
  getTax: () => number;
  getShipping: () => number;
  getTotal: () => number;
  checkout: (userId: string, prediction?: any) => Promise<any>;
}

const CartContext = createContext<CartContextType | undefined>(undefined);

// Cart reducer
const cartReducer = (state: CartState, action: CartAction): CartState => {
  switch (action.type) {
    case 'ADD_ITEM': {
      const { product, quantity } = action.payload;
      const existingItem = state.items.find(item => item.product.id === product.id);
      
      if (existingItem) {
        return {
          ...state,
          items: state.items.map(item =>
            item.product.id === product.id
              ? { ...item, quantity: item.quantity + quantity }
              : item
          )
        };
      } else {
        return {
          ...state,
          items: [...state.items, { product, quantity }]
        };
      }
    }
    
    case 'REMOVE_ITEM': {
      return {
        ...state,
        items: state.items.filter(item => item.product.id !== action.payload.productId)
      };
    }
    
    case 'UPDATE_QUANTITY': {
      const { productId, quantity } = action.payload;
      if (quantity <= 0) {
        return {
          ...state,
          items: state.items.filter(item => item.product.id !== productId)
        };
      }
      
      return {
        ...state,
        items: state.items.map(item =>
          item.product.id === productId
            ? { ...item, quantity }
            : item
        )
      };
    }
    
    case 'CLEAR_CART':
      return {
        ...state,
        items: []
      };
    
    case 'TOGGLE_CART':
      return {
        ...state,
        isOpen: !state.isOpen
      };
    
    case 'CLOSE_CART':
      return {
        ...state,
        isOpen: false
      };
    
    case 'LOAD_CART':
      return {
        ...state,
        items: action.payload
      };
    
    default:
      return state;
  }
};

// Tax rate (8.5% - adjust as needed)
const TAX_RATE = 0.085;

// Shipping calculation
const calculateShipping = (subtotal: number): number => {
  if (subtotal >= 50) {
    return 0; // Free shipping over $50
  }
  return 5.99; // Standard shipping
};

export const CartProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [state, dispatch] = useReducer(cartReducer, {
    items: [],
    isOpen: false
  });

  // Load cart from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      try {
        const cartItems = JSON.parse(savedCart);
        dispatch({ type: 'LOAD_CART', payload: cartItems });
      } catch (error) {
        console.error('Error loading cart from localStorage:', error);
      }
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(state.items));
  }, [state.items]);

  const addItem = (product: Product, quantity: number) => {
    dispatch({ type: 'ADD_ITEM', payload: { product, quantity } });
  };

  const removeItem = (productId: string) => {
    dispatch({ type: 'REMOVE_ITEM', payload: { productId } });
  };

  const updateQuantity = (productId: string, quantity: number) => {
    dispatch({ type: 'UPDATE_QUANTITY', payload: { productId, quantity } });
  };

  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
  };

  const toggleCart = () => {
    dispatch({ type: 'TOGGLE_CART' });
  };

  const closeCart = () => {
    dispatch({ type: 'CLOSE_CART' });
  };

  const getItemQuantity = (productId: string): number => {
    const item = state.items.find(item => item.product.id === productId);
    return item ? item.quantity : 0;
  };

  const getTotalItems = (): number => {
    return state.items.reduce((total, item) => total + item.quantity, 0);
  };

  const getSubtotal = (): number => {
    return state.items.reduce((total, item) => total + (item.product.price * item.quantity), 0);
  };

  const getTax = (): number => {
    return getSubtotal() * TAX_RATE;
  };

  const getShipping = (): number => {
    return calculateShipping(getSubtotal());
  };

  const getTotal = (): number => {
    return getSubtotal() + getTax() + getShipping();
  };

  const checkout = async (userId: string, prediction?: any): Promise<any> => {
    try {
      const response = await fetch('/api/orders/checkout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({
          items: state.items.map(item => ({
            productId: item.product.id,
            quantity: item.quantity,
            price: item.product.price
          })),
          userId: userId,
          subtotal: getSubtotal(),
          tax: getTax(),
          shipping: getShipping(),
          total: getTotal(),
          prediction: prediction
        }),
      });

      if (!response.ok) {
        throw new Error('Checkout failed');
      }

      const order = await response.json();
      clearCart(); // Clear cart after successful checkout
      return order;
    } catch (error) {
      console.error('Checkout error:', error);
      throw error;
    }
  };

  const value: CartContextType = {
    state,
    addItem,
    removeItem,
    updateQuantity,
    clearCart,
    toggleCart,
    closeCart,
    getItemQuantity,
    getTotalItems,
    getSubtotal,
    getTax,
    getShipping,
    getTotal,
    checkout
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};

export const useCart = (): CartContextType => {
  const context = useContext(CartContext);
  if (context === undefined) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';

interface ZipContextType {
  zip: string;
  updateZip: (newZip: string) => void;
  isValidZip: (zip: string) => boolean;
  isUsingLocation: boolean;
  setUsingLocation: (using: boolean) => void;
}

const ZipContext = createContext<ZipContextType | undefined>(undefined);

interface ZipProviderProps {
  children: ReactNode;
}

export const ZipProvider: React.FC<ZipProviderProps> = ({ children }) => {
  const [zip, setZip] = useState<string>('12345'); // Default ZIP
  const [isUsingLocation, setIsUsingLocation] = useState<boolean>(false);

  // Load ZIP from localStorage on mount
  useEffect(() => {
    const savedZip = localStorage.getItem('userZip');
    if (savedZip) {
      setZip(savedZip);
    } else {
      // Try to get ZIP from browser geolocation or IP
      // For now, we'll use a default
      setZip('12345');
    }
  }, []);

  // Save ZIP to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('userZip', zip);
  }, [zip]);

  const isValidZip = (zipCode: string): boolean => {
    const zipRegex = /^\d{5}(-\d{4})?$/;
    return zipRegex.test(zipCode);
  };

  const updateZip = (newZip: string) => {
    if (isValidZip(newZip)) {
      setZip(newZip);
    } else {
      console.warn('Invalid ZIP code format:', newZip);
    }
  };

  const value: ZipContextType = {
    zip,
    updateZip,
    isValidZip,
    isUsingLocation,
    setUsingLocation: setIsUsingLocation,
  };

  return (
    <ZipContext.Provider value={value}>
      {children}
    </ZipContext.Provider>
  );
};

export const useZip = (): ZipContextType => {
  const context = useContext(ZipContext);
  if (context === undefined) {
    throw new Error('useZip must be used within a ZipProvider');
  }
  return context;
};

