    });

    if (!vendor) {
      return res.status(404).json({
        success: false,
        error: 'Vendor not found'
      });
    }

    let data: AnalyticsTrend[] = [];
    switch (range) {
      case 'daily':
        data = await getDailyTrends(vendorId);
        break;
      case 'weekly':
        data = await getWeeklyTrends(vendorId);
        break;
      case 'monthly':
        data = await getMonthlyTrends(vendorId);
        break;
      default:
        data = await getDailyTrends(vendorId);
    }

    res.json({
      success: true,
      data,
      meta: {
