    'Sourdough Bread', 'Croissants', 'Blueberry Muffins', 'Chocolate Chip Cookies',
    'Artisan Pizza', 'Cinnamon Rolls', 'Bagels', 'Danish Pastries'
  ];

  for (const name of productNames) {
